import React, { useEffect, useRef } from 'react';
import "./ExperienceCard.scss";

export default function ExperienceCard({ workExperiences, isDark }) {
  const timelineRef = useRef();

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('show');
          } else {
            entry.target.classList.remove('show');
          }
        });
      },
      { threshold: 0.1 }
    );

    const elements = timelineRef.current.querySelectorAll('.timeline-card');
    elements.forEach((el) => observer.observe(el));

    return () => {
      elements.forEach((el) => observer.unobserve(el));
    };
  }, []);

  return (
    <div className="timeline-container">
      <div className="timeline">
        {workExperiences.map((cardInfo, index) => (
          <div className="timeline-entry" key={index}>
            <div className="timeline-bullet"></div>
            <div className="timeline-card">
              <img src={item.image} alt={`Card ${index + 1}`} />
              <ul>
                {item.points.map((point, idx) => (
                  <li key={idx}>{point}</li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
