import React, { useRef, useEffect, useState } from 'react';
import './Timeline.scss';

const Timeline = ({ experiences }) => {
  const timelineRef = useRef(null);
  const bulletRefs = useRef([]);
  const cardRefs = useRef([]);
  const [activeBullet, setActiveBullet] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const timelineScrollTop = timelineRef.current.scrollTop;
      const bulletHeights = bulletRefs.current.map((bullet) => bullet.offsetTop);

      let newActiveBullet = 0;
      for (let i = 0; i < bulletHeights.length; i++) {
        if (timelineScrollTop >= bulletHeights[i]) {
          newActiveBullet = i;
        } else {
          break;
        }
      }

      setActiveBullet(newActiveBullet);
    };

    timelineRef.current.addEventListener('scroll', handleScroll);

    return () => {
      timelineRef.current.removeEventListener('scroll', handleScroll);   

    };
  }, []);

  const handleBulletClick = (index) => {
    setActiveBullet(index);
    const card = cardRefs.current[index];
    card.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="timeline-container">
      <div className="timeline-bullets" ref={timelineRef}>
        {experiences.map((experience, index) => (
          <div
            key={index}
            className={`bullet ${index === activeBullet ? 'active' : ''}`}
            onClick={() => handleBulletClick(index)}
            ref={(el) => (bulletRefs.current[index] = el)}
          >
            <span>{index + 1}</span>
          </div>
        ))}
        <div className="line"></div>
      </div>
      <div className="timeline-cards">
        {experiences.map((experience, index) => (
          <div
            key={index}
            className={`card ${index === activeBullet ? 'active' : ''}`}
            ref={(el) => (cardRefs.current[index] = el)}
          >
            <div className="card-top">
              <img src={experience.imageLogo} alt={experience.companyName} />
            </div>
            <div className="card-bottom">
              <h3>{experience.title}</h3>
              <ul>
                {experience.workDone.map((item, i) => (
                  <li key={i}>{item}</li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Timeline;
